import { Component, OnInit } from '@angular/core';
import * as L from 'leaflet';
import { Restaurante } from 'src/app/auth/interfaces/restaurante.interfaces';
import { SearchService } from 'src/app/auth/services/search.service';
@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
})
export class HomeComponent implements OnInit {
  constructor(private searchService: SearchService) {}

  ngOnInit(): void {
    this.initMap();
  }

  searchTerm!: string;
  searchCityTerm!: string;
  results: any[] = [];
  mapInitialized: boolean = false;

  search() {
    this.results = [];
    if (this.searchCityTerm === undefined) {
      this.searchCityTerm = 'Madrid';
    }
    this.searchService
      .getRestaurantesPorCiudadYNombre(this.searchTerm, this.searchCityTerm)
      .subscribe((restaurantes: any) => {
        if (restaurantes.ok) {
          restaurantes.restaurantes.forEach((element: Restaurante) => {
            this.results.push(element);
          });
        }
      });
    if (!this.mapInitialized) {
      this.initMap();
      this.mapInitialized = true;
    }
  }

  initMap() {
    const map = L.map('map').setView([51.505, -0.09], 13);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      crs: L.CRS.EPSG4326 // Configurar la proyección explícitamente
      attribution:
        '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
    }).addTo(map);
  }
}
