import { Component } from '@angular/core';
import * as L from 'leaflet';
import { Restaurante } from 'src/app/auth/interfaces/restaurante.interfaces';
import { SearchService } from 'src/app/auth/services/search.service';
@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
})
export class HomeComponent {
  constructor(private searchService: SearchService) {}

  searchTerm!: string;
  searchCityTerm!: string;
  results: any[] = [];

  search() {
    this.results = [];
    if (this.searchCityTerm === undefined) {
      this.searchCityTerm = 'Madrid';
    }
    this.searchService
      .getRestaurantesPorCiudadYNombre(this.searchTerm, this.searchCityTerm)
      .subscribe((restaurantes: any) => {
        if (restaurantes.ok) {
          console.log(restaurantes);
          restaurantes.restaurantes.forEach((element: Restaurante) => {
            this.results.push(element);
          });
        }
      });
    this.initMap();
    console.log(this.results[0]);
  }

  initMap() {
    const map = L.map('map').setView([51.505, -0.09], 13);

    L.tileLayer('https://tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: 'Mapa de los resultados',
      maxZoom: 13,
    }).addTo(map);

    const marker = L.marker([51.5, -0.09]).addTo(map);
    marker.bindPopup('<b>Hello world!</b><br>I am a popup.').openPopup();
  }
}
<div class="busqueda">
  <div class="search-container">
    <input
      class="search-input"
      [(ngModel)]="searchCityTerm"
      type="text"
      placeholder="¿Dónde quieres comer?"
    />
  </div>
  <span class="spacer"></span>
  <div class="search-container">
    <input
      class="search-input"
      [(ngModel)]="searchTerm"
      type="text"
      placeholder="¿En que restaurante?"
    />
    <button class="search-button" (click)="search()">
      <i class="fas fa-search"></i>
    </button>
  </div>
</div>
<div id="map"></div>

<div class="contenedor" *ngIf="results.length != 0; else mensajeBuscar">
  <div class="results-container">
    <h2>Resultados</h2>
    <ul>
      <li *ngFor="let result of results">
        <mat-card class="example-card">
          <mat-card-header>
            <div mat-card-avatar class="example-header-image"></div>
            <mat-card-title>{{ result.nombre }}</mat-card-title>
            <mat-card-subtitle>{{ result.tematica }}</mat-card-subtitle>
          </mat-card-header>
          <mat-card-content>
            <h3><b>Comentarios de los usuarios:</b></h3>
            <li *ngFor="let comentario of result.comentarios">
              {{ comentario }}
            </li>
          </mat-card-content>
          <mat-card-actions>
            <button mat-button>Ver Restaurante</button>
            <button mat-button>Guardar en Favoritos</button>
          </mat-card-actions>
        </mat-card>
      </li>
    </ul>
  </div>
</div>

<ng-template #mensajeBuscar>
  <div class="mensaje">Comienza a buscar restaurantes</div>
</ng-template>
