import { Component, Inject, OnInit } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import {
  MAT_DIALOG_DATA,
  MatDialog,
  MatDialogRef,
} from '@angular/material/dialog';
import { Router } from '@angular/router';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
})
export class LoginComponent implements OnInit {
  registrarUsuario: Boolean = false;
  hide: Boolean = true;

  constructor(
    private router: Router,
    private fb: FormBuilder,
    public dialog: MatDialog,
    private authService: AuthService
  ) {}

  ngOnInit(): void {
    if (this.authService.validarToken()) {
      this.router.navigateByUrl('/dashboard');
    }
  }

  public loginForm = this.fb.group({
    email: ['', [Validators.required, Validators.email]],
    password: ['', [Validators.required]],
  });
  submited = false;

  openDialog(err: any): void {
    const dialogRef = this.dialog.open(DialogComponent, {
      width: '300px',
      data: { err: err },
    });

    dialogRef.afterClosed().subscribe((result) => {});
  }

  login() {
    const { email, password } = this.loginForm.value;
    this.authService.login(email!, password!).subscribe(
      (resp) => {
        //TODO Abrir página de login con datos del usuario
        //* Recuperar objeto base de datos y cargar página según datos
        console.log(resp);
        if (resp) {
          this.router.navigateByUrl('/dashboard');
        }
      },
      (err) => {
        let erroresEnviar: any = [];
        let formErrors = err.error.errores === undefined;
        if (!formErrors) {
          // Errores al checkear si estan vacios email, contraseña y si el email está bien formado
          Object.entries(err.error.errores).forEach(([key, value]) => {
            console.log(value);
            erroresEnviar.push(value);
          });
        } else {
          // Errores del backend: pj usuario o contraseña incorrectos
          erroresEnviar.push(err.error);
        }
        this.openDialog(erroresEnviar);
      }
    );
  }
}

@Component({
  selector: 'app-dialog',
  templateUrl: 'dialog.html',
})
export class DialogComponent {
  constructor(
    public dialogRef: MatDialogRef<DialogComponent>,
    @Inject(MAT_DIALOG_DATA) public data: any
  ) {}

  onNoClick(): void {
    this.dialogRef.close();
  }
}
<h1 mat-dialog-title>Errores en formulario</h1>
<div mat-dialog-content>
  <li *ngFor="let error of data.err">{{error.msg}}</li>
</div>
<div mat-dialog-actions align="end">
  <button mat-raised-button color="accent" (click)="onNoClick()">Cerrar</button>
</div>
<mat-drawer-container class="example-container" autosize>
  <mat-drawer #drawer class="example-sidenav" mode="over">
    <mat-card class="example-card">
      <mat-card-header>
        <mat-card-title>Inicio de Sesión</mat-card-title>
        <mat-card-subtitle
          >Regístrate si no tienes una cuenta!</mat-card-subtitle
        >
      </mat-card-header>
      <mat-card-content>
        <form [formGroup]="loginForm" (ngSubmit)="login()" id="loginForm">
          <div class="example-container">
            <mat-form-field appearance="fill">
              <mat-label>Introduce tu email</mat-label>
              <input
                autocomplete="off"
                type="email"
                formControlName="email"
                matInput
                placeholder="tuCorreo@dominio.com"
                required
              />
            </mat-form-field>
          </div>
          <br />
          <div class="example-container">
            <mat-form-field appearance="fill">
              <mat-label>Introduce tu contraseña</mat-label>
              <input
                autocomplete="off"
                type="password"
                formControlName="password"
                matInput
                [type]="hide ? 'password' : 'text'"
                required
              />
              <button
                type="button"
                mat-icon-button
                matSuffix
                (click)="hide = !hide"
                [attr.aria-label]="'Hide password'"
                [attr.aria-pressed]="hide"
              >
                <mat-icon>{{
                  hide ? "visibility_off" : "visibility"
                }}</mat-icon>
              </button>
            </mat-form-field>
          </div>
        </form>
      </mat-card-content>
      <mat-card-actions>
        <button
          mat-raised-button
          color="accent"
          type="submit"
          (click)="login()"
        >
          Iniciar Sesión
        </button>
        <button
          mat-raised-button
          color="accent"
          routerLink="/auth/registro"
          routerLinkActive="active"
          ariaCurrentWhenActive="page"
        >
          Registrarse
        </button>
      </mat-card-actions>
    </mat-card>
  </mat-drawer>

  <div class="example-sidenav-content">
    <button
      type="button"
      mat-raised-button
      color="primary"
      (click)="drawer.toggle()"
    >
      Iniciar Sesión
    </button>
  </div>
</mat-drawer-container>
